@AbapCatalog.viewEnhancementCategory: [#NONE]
@AccessControl.authorizationCheck: #NOT_REQUIRED
@EndUserText.label: 'BSIK View'
@Metadata.ignorePropagatedAnnotations: true
@ObjectModel.usageType:{
    serviceQuality: #X,
    sizeCategory: #S,
    dataClass: #MIXED
}
define view entity ZETR_RECO_DDL_BSIKBSAK
  as select from I_JournalEntryItem        as bkpf
    inner join   I_OperationalAcctgDocItem as bseg on  bseg.CompanyCode        = bkpf.CompanyCode
                                                   and bseg.AccountingDocument = bkpf.AccountingDocument
                                                   and bseg.AccountingDocumentItem = bkpf.AccountingDocumentItem
                                                   and bseg.FiscalYear         = bkpf.FiscalYear
{
  key bkpf.CompanyCode,
  key bkpf.Supplier,
        key bseg.SpecialGLTransactionType,
  key bkpf.SpecialGLCode,
  key bkpf.ClearingDate,
  key bkpf.ClearingJournalEntry,
  key bkpf.AssignmentReference,
  key bkpf.FiscalYear,
  key bkpf.AccountingDocument,
  key bkpf.AccountingDocumentItem,
      bseg.DueCalculationBaseDate,
      bseg.CashDiscount1Days,
      bkpf.PostingDate,
      bkpf.DocumentDate,
      bkpf.DebitCreditCode,
      bkpf.AccountingDocumentType,
      bkpf.AccountingDocCreatedByUser,
      //      @Semantics: { amount : {currencyCode: 'CompanyCodeCurrency'} }
      //      bkpf.AbsoluteAmountInCoCodeCrcy, //dmbtr
      bkpf.CompanyCodeCurrency,
      //      @Semantics: { amount : {currencyCode: 'TransactionCurrency'} }
      //      bseg.AbsoluteAmountInTransacCrcy, //wrbtr
      bkpf.TransactionCurrency,
      @Semantics: { amount : {currencyCode: 'AdditionalCurrency1'} }
      bseg.AbsltAmtInAdditionalCurrency1, //dmbe2
      bseg.AdditionalCurrency1,
      @Semantics: { amount : {currencyCode: 'AdditionalCurrency2'} }
      bseg.AbsltAmtInAdditionalCurrency2, //dmbe3
      bseg.AdditionalCurrency2,
      bkpf.DocumentItemText,
      bseg.PaymentMethod,
      bkpf.PurchasingDocument,
      bseg.PaymentBlockingReason,
      bseg.PaymentTerms,
      bkpf.BusinessArea,
//      bseg.AbsoluteExchangeRate,
      bseg.OriginalReferenceDocument,
//      bseg.ParkedByUser,
      bkpf.IsReversal,
      bkpf.IsReversed,
//      bseg.DocumentReferenceID,
      bkpf.GLAccount,
      bseg.CashDiscount2Days,
      bseg.NetPaymentDays,
      bseg.OperationalGLAccount,
      @Semantics: { amount : {currencyCode: 'CompanyCodeCurrency'} }
      bkpf.AmountInCompanyCodeCurrency,
      @Semantics: { amount : {currencyCode: 'CompanyCodeCurrency'} }
      bkpf.AmountInTransactionCurrency
}
where
      bkpf.FinancialAccountType = 'K'
  and bkpf.SpecialGLCode        = ''
  and bkpf.Ledger               = '0L'
